    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        new Thread(){
            public void run(){
                int i = 0;
                while (i < 101){
                    lblTime.setText(String.valueOf(i) + "%");
                    lblMssg.setText("Seu sistema estÃ¡ sendo carregado, aguarde um momento...");
                    i++;
                    try{
                        sleep(20);
                    } catch (InterruptedException ex) {
                        Logger.getLogger(TelaSplash.class.getName()).log(Level.SEVERE, null, ex);
                    }
                   }
                TelaSplash.this.dispose();
                TelaLogin x = new TelaLogin();
                x.setVisible(true);
                
            }            
        }.start();

    }//GEN-LAST:event_formWindowOpened

